#! /usr/bin/env node

// DO NOT EDIT THIS FILE, THIS FILE IS FOR CREATING SUBATOMIC PROJECTS, EDITING THE FILE MAY CAUSE PROBLEMS

const { exec } = require('child_process');
const repoUrl = 'https://github.com/TheLazyCodernothacker/Subatomic.git';
const destinationFolder = 'subatomic';


console.log('Creating a subatomic project...');


const cloneCommand = `git clone ${repoUrl} ${destinationFolder}`;
const childProcess = exec(cloneCommand);


childProcess.stdout.on('data', (data) => {
  console.log(data.toString());
});

childProcess.stderr.on('data', (data) => {
  console.error(data.toString());
});

// Handle the completion of the cloning process
childProcess.on('close', (code) => {
  if (code === 0) {
    console.log('Creating completed successfully.');
    console.log('To get started: type "cd subatomic"');
    console.log('Then type "npm i && npm start"');

  } else {
    console.error(`Error during cloning. Exit code: ${code}`);
  }
});
